library(data.table) # needed for fread()

#All in one step:
# Download 
# Unzip
# Grep the header and the 2 days of interest
# Convert ? into NA
data2days <- fread("curl https://d396qusza40orc.cloudfront.net/exdata%2Fdata%2Fhousehold_power_consumption.zip  | funzip  | egrep -w 'Date|1/2/2007|2/2/2007'",
                   na.strings="?")

# call dev.copy() before plot() call to avoid Error in plot.new()
dev.copy( png, file="plot4.png", width=480, height=480)

# combine Time and Date in POSIXct format (more compact as the POSIXlt generated by strptime())
datetimePOSIXct <- with( data2days, as.POSIXct(paste(Date, Time), format="%d/%m/%Y %H:%M:%S") )

# create 2 by 2 grid layout for the 4 plots 
par(mfrow = c(2, 2)) 

# First plot:  from plot2.R
plot( datetimePOSIXct, data2days$Global_active_power, 
      type="l", xlab="", ylab="Global Active Power" )

# Second plot:  same as plot2 with y=Voltage
plot( datetimePOSIXct, data2days$Voltage, 
      type="l", xlab="datetime", ylab="Voltage" )

# Third plot:  from plot3.R
plot(  datetimePOSIXct, data2days$Sub_metering_1, type="l", ylab="Energy sub metering", xlab="")
lines( datetimePOSIXct, data2days$Sub_metering_2, type="l", col="red")
lines( datetimePOSIXct, data2days$Sub_metering_3, type="l", col="blue")
legend( "topright", 
        c("Sub_metering_1", "Sub_metering_2", "Sub_metering_3"), 
        lty=1, lwd=2.5, col=c("black", "red", "blue") )

# Fourth plot:  same as plot2 with y=Global_reactive_power
plot( datetimePOSIXct, data2days$Global_reactive_power, 
      type="l", xlab="datetime", ylab="Global_reactive_power" )

# close png device
dev.off()
